    var request = new XMLHttpRequest()

request.open('GET',String('https://api.1inch.exchange/v3.0/1/quote?fromTokenAddress='+address1.text+'&toTokenAddress='+address2.text+'&amount=1'), true)
request.onload = function () {
  // Begin accessing JSON data here
  var data = JSON.parse(this.response)

  if (request.status >= 200 && request.status < 400) {

    console.log(data)
    // find decimal points
    var decimals1 = data.fromToken.decimals
    var decimals2 = data.toToken.decimals
    // calculate price
    if (decimals2 > decimals1){
      var decimalsDiff = decimals2 - decimals1
      var price = data.toTokenAmount/Math.pow(10, decimalsDiff);
      console.log(price)
      price3d.set({'text':String(price)})
      price_result.set({'text':String('The price of 1 '+data.fromToken.symbol+' is equal to '+price+data.toToken.symbol)})
    }



    // update polytext
    //price3d.set({'text':String(price)})

  } else {
    console.log('error')
  }
}

request.send()
})

