let textChange = parcel.getFeatureById('textChange')

feature.on('click',e=>{
    var request = new XMLHttpRequest()

request.open('GET', 'https://api.cryptonator.com/api/ticker/eth-usd', true)
request.onload = function () {
  // Begin accessing JSON data here
  var data = Number(JSON.parse(this.response).ticker.price).toFixed(2)

  if (request.status >= 200 && request.status < 400) {
    console.log(data)
   // update polytext
    textChange.set({'text':String(data)})
  } else {
    console.log('error')
  }
}

request.send()
})

(async () => {
   const price = await fetch('https://api.cryptonator.com/api/ticker/eth-usd').then(response => response.json());

   newPrice = Number(price.ticker.price).toFixed(2)

   feature.set({'text':String(newPrice)})
})();

