// Creates features
async function createFeature(attributes) {
  console.log( 'createFeature', attributes )
  let a = parcel.createFeature(attributes.type) 
  if (attributes['boundTo']){
    let parentFeature = parcel.getFeatureById(attributes.boundTo) 
    let p = Object.assign({}, parentFeature.position); 
    if ( attributes.type == 'richtext' ){
	  p.y = p.y + 2.7 
	  a.set({'text': attributes['text'], 'blendMode': 'screen'})
	}
	if ( attributes.type == 'button' ){
	  if( ( attributes.p && attributes.p == 'right') ){ p.z = p.z + .35 }
	  else{ p.z = p.z - .5 } 
	  p.y = p.y + 2.25
      a.on('click', async e=> { 
	    await setState(e, attributes.boundTo, attributes.id); 
		await handleFeatures(e); 
	  })	  
	} 
	a.set( { scale:attributes.s, position:[p.x, p.y, p.z], rotation:attributes.r, id:attributes.id } )
  }
  return a
}


