features.script:
let key = parcel.getFeatureById('key-btn'), guardian = parcel.getFeatureById('guardian-btn'), discharge = parcel.getFeatureById('discharge-btn'), rsign = parcel.getFeatureById('rick-sign'), jd = parcel.getFeatureById('jail-door'), jdmove = jd.createAnimation('position'), gsign = parcel.getFeatureById('guardsign'), keys = parcel.getFeatureById('keysign'), cd = parcel.getFeatureById('certdis'), rthes = parcel.getFeatureById('rickthescientist'), rthesMove = rthes.createAnimation('position'), walletCollect = [];

waitForDoneo1 = (n, s) => {return new Promise(resolve => {setTimeout(() => {resolve(n)}, s);}).catch(e => console.error(e.message));};

guardianRick = async () => {
  try {
    let waitRick = waitForDoneo1(waiting = true, 8000);
    await waitRick;  
  }
  catch (e) {console.error(e);}
  console.log('rick has been freed!');
  gsign.position.y = 0;
  keys.position.y = 0;
  //cd.position.y = 0;
  rickTeleports();
};

rickTeleports = async () => {
  try {
    let waitRick = waitForDoneo1(waiting = true, 1500);
    await waitRick;  
  }
  catch (e) {console.error(e);}
  console.log('rick is leaving!');
  rthesMove.setKeys([{
      frame: 10,
      value: rthes.position.add(new Vector3(0,-2,0))
    }]);
  rthes.startAnimations( [rthesMove]);
  rthes.position.y = 0;
};

guardian.on('click', e => {
  console.log('clicked guardian btn!');
  if (rsign.get('guard') === true) {
    console.log('guardian key already pressed');
  }
  else if (rsign.get('keyed') === true) {    
    console.log('key was pressed so guardian activated');
    rsign.set({guard: true});
    gsign.position.y = 1.8;
    console.log('wallet:', e.player.wallet);
    walletCollect.push(e.player.wallet);
    console.log('wallet from array:', walletCollect[0]);  
    guardianRick();  
  } 
});

let key = parcel.getFeatureById('key-btn'), guardian = parcel.getFeatureById('guardian-btn'), discharge = parcel.getFeatureById('discharge-btn'), rsign = parcel.getFeatureById('rick-sign'), keys = parcel.getFeatureById('keysign'), jd = parcel.getFeatureById('jail-door'), jdmove = jd.createAnimation('position'), rthes = parcel.getFeatureById('rickthescientist'), rthesMove = rthes.createAnimation('position'), cd = parcel.getFeatureById('certdis');

waitForDoneo1 = (n, s) => {return new Promise(resolve => {setTimeout(() => {resolve(n)}, s);}).catch(e => console.error(e.message));};

moveJaildoor = async () => {
  try {
    let waitRick = waitForDoneo1(waiting = true, 2000);
    await waitRick;  
  }
  catch (e) {console.error(e);}
  console.log('moving jail door!');
  keys.position.y = 0;
  jdmove.setKeys([{
      frame: 10,
      value: jd.position.add(new Vector3(0,-2,0))
    }]);
  jd.startAnimations( [jdmove]);
  jd.position.y = 0;
  rickLeaves();
};

rickLeaves = async () => {
  try {
    let waitRick = waitForDoneo1(waiting = true, 1000);
    await waitRick;  
  }
  catch (e) {console.error(e);}
  console.log('rick is leaving!');
  rthesMove.setKeys([{
      frame: 10,
      value: rthes.position.add(new Vector3(0,0,3))
    }]);
  rthes.startAnimations( [rthesMove]);
  rthes.position.z = 3;
  rsign.set({keyed: true});
};

key.on('click', e => {
  if (rsign.get('keyed') === true) {
    console.log('already pressed key btn');
  }
  else if (rsign.get('dis') === true) {
        console.log('discharge button pressed - key activated');
        cd.position.y = 0;  
        keys.position.y = 1.8;
        moveJaildoor();    
  }  
});

let discharge = parcel.getFeatureById('discharge-btn')
let cd = parcel.getFeatureById('certdis');

discharge.on('click', async e => { 
  console.log('clicked on discharge so setting dis to true on rsign');  
  cd.position.y = 1.8;    

  window = this
  importScripts( 'https://cdn.jsdelivr.net/npm/gun/gun.js', 'https://cdn.jsdelivr.net/npm/gun/lib/then.js', "https://cdn.jsdelivr.net/npm/gun/lib/radix.js", "https://cdn.jsdelivr.net/npm/gun/lib/radisk.js", "https://cdn.jsdelivr.net/npm/gun/lib/store.js", "https://cdn.jsdelivr.net/npm/gun/lib/rindexed.js" )

  let gun = Gun({ localStorage: false, radisk: true })
  const gameData = await gun.get('chronos_minigame');
  console.log('gameData', gameData)

  const game = gun.get('chronos_minigame')  
  game.put({ winter: true })
});
