function check_for_hit(player_position,coin_index){

          let dist = 0.3
           y_offset = 0

           if(  coin[coin_index].position.x >= (player_position.x - dist) && 
           coin[coin_index].position.x <= (player_position.x + dist) && 
           coin[coin_index].position.y >= (y_offset + player_position.y - 1.6) && 
           coin[coin_index].position.y <= (y_offset + player_position.y) && 
           coin[coin_index].position.z >= (player_position.z - dist) && 
           coin[coin_index].position.z <= (player_position.z + dist)
           ){

                    if(coin[coin_index].metadata.hit == false){
                          coin[coin_index].metadata.hit = true
                          coin[coin_index].position.x =  6
                          coin[coin_index].position.z = -4.5
                          coin[coin_index].position.y = 1.5 

                          console.log("HIT HIT HIT!!!")

                          audio.play()
                   }    
           }    
}



