feature.on('click', e=>{



    let phi = -e.player.rotation.x + Math.PI/2 + Math.PI;
    let theta = -e.player.rotation.y + Math.PI/2 + Math.PI;
    let xDelta = 1*Math.cos(theta) * Math.sin(phi);
    let zDelta = 1*Math.sin(theta) * Math.sin(phi);
    let yDelta = 1*Math.cos(phi);
    let radius = 60;
    let xDelta2 = radius * Math.cos(theta) * Math.sin(phi);
    let zDelta2 = radius  * Math.sin(theta) * Math.sin(phi);
    let yDelta2 = radius * Math.cos(phi);
    let start_xyz = [e.player.position.x + xDelta, e.player.position.y + yDelta, e.player.position.z + zDelta];
    let end_xyz = [e.player.position.x + xDelta2,e.player.position.y + yDelta2,e.player.position.z + zDelta2];

    let next_id = get_next(cats,cats_length);

    if(next_id <= 3){
       cats[next_id].metadata.stuff.active = true
       cats[next_id].metadata.stuff.start_time = new Date().getTime()
       cats[next_id].metadata.stuff.start_xyz = start_xyz
       cats[next_id].metadata.stuff.end_xyz = end_xyz

       console.log("cat",next_id,"initiated.")  

    }else{
       console.log("OVER 3!!!! OVER 3!!!")
    }


})


function get_next(cats,cats_length){
    for (index = 0; index < cats_length; index++) {
        if(cats[index].metadata.stuff.active == false){
             return index
        }
   }
}


global_interval = setInterval(()=>{

            for(let index = 0; index < cats_length; index++){ 
                if(cats[index].metadata.stuff.active == true){
                    update_cat(index)    
                }   
           }

}, 40)


function update_cat(index){

     let current_time = new Date().getTime();
     let start_time = cats[index].metadata.stuff.start_time;
     let diff_time = (current_time - start_time)/1000;      


     if(diff_time <= 0.5){ 
           let start_xyz = cats[index].metadata.stuff.start_xyz;
           let end_xyz = cats[index].metadata.stuff.end_xyz; 
           set_new_position(start_xyz, end_xyz, current_time, diff_time, index);

           console.log("moving",diff_time);    

     }else if(diff_time > 0.5 && diff_time <= 0.7){

          console.log("fast_burst",index,"-",diff_time);    
          update_boom(index,0.7)

    }else if(diff_time > 0.7 && diff_time <= 1.4){  

          console.log("slow_burst",index,"-",diff_time);      
          update_boom(index,0.15)      

    }else{

           console.log("breaking",index," - ",diff_time);      

           break_cat(index)

           booms[index].set({scale:[1,1,1]});

          booms[index].set({position:[booms[index].metadata.stuff.default_xyz[0], booms[index].metadata.stuff.default_xyz[1], booms[index].metadata.stuff.default_xyz[2]]});



     }
}


function update_boom(index,speed){

   booms[index].set({position:[cats[index].position.x, cats[index].position.y-1, cats[index].position.z]});

   booms[index].scale.x += speed;
   booms[index].scale.y += speed;
   booms[index].scale.z += speed;

}




function set_new_position(start_xyz, end_xyz, current_time, diff_time, index) {

   let new_x_pos = start_xyz[0] + (end_xyz[0] - start_xyz[0]) * diff_time + 0.05;
   let new_y_pos = start_xyz[1] + (end_xyz[1] - start_xyz[1]) * diff_time - 0.125 + (0.5 * -20 * diff_time*diff_time);
   let new_z_pos = start_xyz[2] + (end_xyz[2] - start_xyz[2]) * diff_time;

   cats[index].set({position:[new_x_pos, new_y_pos, new_z_pos]});
}


function break_cat(index){


    cats[index].metadata.stuff.active = false

    cats[index].set({position:[cats[index].metadata.stuff.default_xyz[0], cats[index].metadata.stuff.default_xyz[1], cats[index].metadata.stuff.default_xyz[2]]});



}

