 });

let Distance_to_player=1.5 // How far the object is relative to the player (negative will be behind)
let up_down_constant= -0  // Positive object will go higher, negative will go lower. eg: -0.65 places the object where your hands are, approximately. 0 will place the object right above your eyes.
let refresh_rate = 50       // Number of milliseconds. Please avoid a refresh rate < 30 as it could crash the grid.
const restOfTheVoxel = [parcel.getFeatureById('vox2'), parcel.getFeatureById('vox3'), parcel.getFeatureById('vox4')]

console.log(restOfTheVoxel)
//let restOfTheVox = parcel.getFeatureById('vox2');

let differenceX = [];
let differenceY = [];
let differenceZ = [];

for (let i = 0; i < restOfTheVoxel.length; i++) {
    differenceX[i] = restOfTheVoxel[i].position.x - feature.position.x;
    differenceY[i] = restOfTheVoxel[i].position.y - feature.position.y;
    differenceZ[i] = restOfTheVoxel[i].position.z - feature.position.z;
}

//let differenceX = [restOfTheVoxel[0].position.x - feature.position.x, restOfTheVoxel[1].position.x - feature.position.x, restOfTheVoxel[2].position.x - feature.position.x]
//let differenceY = [restOfTheVoxel[0].position.y - feature.position.y, restOfTheVoxel[1].position.y - feature.position.y, restOfTheVoxel[2].position.y - feature.position.y]
//let differenceZ = [restOfTheVoxel[0].position.z - feature.position.z, restOfTheVoxel[1].position.z - feature.position.z, restOfTheVoxel[2].position.z - feature.position.z]

let dragging = false;

//-------------- Do not touch -willie ---------------------------

function moveObject(player){ 
    let playerRotation = player.rotation;

    let xDelta = Distance_to_player*Math.cos(-playerRotation.y+Math.PI/2 + Math.PI);
    let yDelta = Distance_to_player*Math.tan(playerRotation.x);
    let zDelta = Distance_to_player*Math.sin(-playerRotation.y+Math.PI/2 + Math.PI);
    var positionRell = [
        player.position.x - xDelta,
        player.position.y - yDelta +(up_down_constant),
        player.position.z - zDelta
    ];

    return positionRell
}

function setPosition(object,e){ // refreshes the positions
    if (!dragging) {
        return;
    }
    object.set({position:moveObject(e.player)})

    for (let i = 0; i < restOfTheVoxel.length; i++) {
        restOfTheVoxel[i].position.x = object.position.x+differenceX[i]
        restOfTheVoxel[i].position.y = object.position.y+differenceY[i]
        restOfTheVoxel[i].position.z = object.position.z+differenceZ[i]
    }
    if(!object.position || !object.rotation){
        parcel.removeFeature(object)
    }else{
        setTimeout(()=>{
        setPosition(object,e)
    },refresh_rate)
  }
}

feature.on('click',e=>{
    if (!dragging){
        dragging = true;
        let draggeableObject = parcel.getFeatureById(feature.id)
        setPosition(draggeableObject,e)
