setInterval(() => {
    if (dragging) {
        if (lastPositionText != currentPositionText) {
            lastPositionText = currentPositionText;
            for (let i = 0; i < bodyText.length; i++) {

                let currentText = bodyString[i + Math.floor(currentPositionText)]
                if (currentText == undefined) {
                    currentText = "";
                }
                bodyText[i].set({'text': currentText})
            }
        }
    }
}, 600)
function getNextCoordinates(player){ 
    let playerRotation = player.rotation;
    let returnValue = 0;
    let yDelta = Distance_to_player*Math.tan(playerRotation.x);

    returnValue = player.position.y - yDelta +(up_down_constant)
    if (returnValue > sliderBarMaxY) {
        returnValue = sliderBarMaxY;
    }

    if (returnValue < sliderBarMinY) {
        returnValue = sliderBarMinY;
    }

    return returnValue;
}

function getTextPosition(coordinate) {
    maxDistance = sliderBarMaxY - sliderBarMinY;
    let percentage = (sliderBarMaxY - coordinate) * 100 / (maxDistance);

    return percentage;
}

