function requestAd(e) {
    var xmlHttp = new XMLHttpRequest();
    xmlHttp.open('POST', 'https://enter.meta-serve.net/delivery/voxels', false);
    xmlHttp.setRequestHeader("Content-Type", "application/json");

    xmlHttp.send(JSON.stringify({
        sizes: [{width: 1080, height: 1920}],
        location: {
            x: 730,
            z: 438,
            y: 2
        },
        index: 3,
        inventory: "voxelRotationCubeLeft",
        player: {
            userId: e.player.wallet,
        },
        publisher: parcel.owner
    }));

    const parsedObject = JSON.parse(xmlHttp.responseText);

    if(parsedObject && parsedObject.impressionTracker && parsedObject.mediaUrl && parsedObject.clickUrl) {
        var xmlHttp2 = new XMLHttpRequest();
        xmlHttp2.open('GET', parsedObject.impressionTracker, false);
        xmlHttp2.send(null);
        feature.set({ url: parsedObject.mediaUrl });
        feature.set({ link: parsedObject.clickUrl });
    }
    else {
        feature.set({ url: "https://media-crvox.sfo2.digitaloceanspaces.com/0xf80fe25f8db66727506f2c0b019f28f5211587be/1653530419492-6daeb074-839e-4670-98ad-c3067bf25a8b.jpg" });
